(function(){tinymce.PluginManager.requireLangPack('biblioref');tinymce.create('tinymce.plugins.BiblioRefPlugin',{init:function(ed,url){ed.addCommand('mceBiblioRef',function(){ed.windowManager.open({file:url+'/dialog.htm',width:320+parseInt(ed.getLang('example.delta_width',0)),height:80+parseInt(ed.getLang('example.delta_height',0)),inline:1},{plugin_url:url,some_custom_arg:'custom arg'})});ed.addButton('biblioref',{title:'biblioref.desc',cmd:'mceBiblioRef',image:url+'/img/cite.gif'})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'Biblio Ref plugin',author:'Some author',authorurl:'http://tinymce.moxiecode.com',infourl:'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/example',version:"1.0"}}});tinymce.PluginManager.add('biblioref',tinymce.plugins.BiblioRefPlugin)})();